import React, { useEffect, useState } from 'react'
import styled from 'styled-components'

const BodyWrapper = styled.div`
  position: relative;
  width: auto;
  background: ${({ theme }) => theme.bg1};
  box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.01), 0px 4px 8px rgba(0, 0, 0, 0.04), 0px 16px 24px rgba(0, 0, 0, 0.04),
    0px 24px 32px rgba(0, 0, 0, 0.01);
  border-radius: 15px;
  padding: 2rem;
  font-size: 110%;
`

const Label = styled.span`
  font-weight: bold;
  color: #8979c5;
  margin-bottom: 0.5rem;
`

const InfoLabel = ({ key, value }) => (
  <Label>
    {key}: {value}
  </Label>
)

export const RPC_ENDPOINT = process.env.REACT_APP_RPC_ENDPOINT

export function ConnectionInfo() {
  const [connectionData, setConnectionData] = useState(true)

  useEffect(() => {
    fetch(`../../../connection-info-by-uuid${RPC_ENDPOINT}`)
      .then((response) => response.json())
      .then((data) => setConnectionData(data))
      .catch((error) => console.error(error))
  }, [])

  return connectionData
}

export default function Connection() {
  const connectionData = ConnectionInfo()
  return (
    <BodyWrapper>
      <InfoLabel key="UUID" value={connectionData['UUID']} />
      <InfoLabel key="RPC URL" value={connectionData['RPC URL']} />
      <InfoLabel key="Player Private Key" value={connectionData['Player Private Key']} />
      <InfoLabel key="Player Address" value={connectionData['Player Address']} />
      <InfoLabel key="Setup Contract" value={connectionData['Setup Contract']} />
      <InfoLabel key="Target Contract" value={connectionData['Target Contract']} />
      <InfoLabel key="WETH Token Contract" value={connectionData['WETH Token Contract']} />
      <InfoLabel key="HTB Token Contract" value={connectionData['HTB Token Contract']} />
    </BodyWrapper>
  )
}
